// ProductCarousel.js
import React, { useState, useEffect } from 'react';
import ProductCard from './productCard'; // Asegúrate de importar la tarjeta de producto

const ProductCarousel = ({ products }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // Solo configurar el intervalo si `products` está disponible
    if (products && products.length > 0) {
      const interval = setInterval(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 3) % products.length); // Cambiar en bloques de 3
      }, 3000);

      return () => clearInterval(interval); // Limpiar el intervalo al desmontarse
    }
  }, [products]);

  const nextSlide = () => {
    if (products && products.length > 0) {
      setCurrentIndex((prevIndex) => (prevIndex + 3) % products.length);
    }
  };

  const prevSlide = () => {
    if (products && products.length > 0) {
      setCurrentIndex((prevIndex) => (prevIndex - 3 + products.length) % products.length);
    }
  };

  // Asegurarse de que products no es undefined antes de usar .slice()
  const displayedProducts = products && products.slice(currentIndex, currentIndex + 3);

  return (
    <div className="product-carousel">
      <div className="carousel-container">
        <button className="carousel-control prev" onClick={prevSlide}>
          &#10094;
        </button>

        <div className="carousel-items">
          {displayedProducts && displayedProducts.length > 0 ? (
            displayedProducts.map((product) => (
              <ProductCard
                key={product._id}
                image={product.image}
                name={product.name}
                price={`$${product.price}`}
                category={product.category}
              />
            ))
          ) : (
            <p>No hay productos disponibles.</p>
          )}
        </div>

        <button className="carousel-control next" onClick={nextSlide}>
          &#10095;
        </button>
      </div>
    </div>
  );
};

export default ProductCarousel;
