import React, { useState, useEffect } from 'react';
import ProductCard from './productCard';

const ProductCarousel = ({ products = [] }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Verificación de productos en consola
  useEffect(() => {
    console.log("Productos recibidos:", products);  // Verificar productos recibidos
  }, [products]);

  // Si no hay productos en el array o aún está vacío, mostrar mensaje de "Cargando..."
  if (!products || products.length === 0) {
    return <p>Cargando productos...</p>;
  }

  // Mostrar solo cuando los productos sean suficientes
  const displayedProducts = products.slice(currentIndex, currentIndex + 3);

  // Cambiar de producto hacia adelante
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 3) % products.length);
  };

  // Cambiar de producto hacia atrás
  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 3 + products.length) % products.length);
  };

  return (
    <div className="product-carousel">
      <div className="carousel-container">
        <button className="carousel-control prev" onClick={prevSlide}>
          &#10094;
        </button>

        <div className="carousel-items">
          {displayedProducts.map((product) => (
            <ProductCard
              key={product._id}
              image={product.image}
              name={product.name}
              price={`$${product.price}`}
              category={product.category}
            />
          ))}
        </div>

        <button className="carousel-control next" onClick={nextSlide}>
          &#10095;
        </button>
      </div>
    </div>
  );
};

export default ProductCarousel;
