import React, { useState, useEffect } from 'react';
import ProductCard from './productCard';

const ProductCarousel = ({ products = [] }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    console.log("Productos recibidos en el carousel:", products);
  }, [products]);


  // Función para pasar a la siguiente "slide"
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 3) % products.length);
  };

  // Función para pasar a la anterior "slide"
  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 3 + products.length) % products.length);
  };

  const displayedProducts = products.slice(currentIndex, currentIndex + 3);  // Mostrar 3 productos a la vez
// Verificar si los productos están disponibles
  if (!products || products.length === 0) {
  return <p>No hay productos disponibles.</p>;
  }
  return (
    <div className="product-carousel">
      <div className="carousel-container">
        <button className="carousel-control prev" onClick={prevSlide}>
          &#10094;
        </button>

        <div className="carousel-items">
          {displayedProducts.map((product) => (
            <ProductCard
              key={product._id}
              image={product.image}
              name={product.name}
              price={`$${product.price}`}
              category={product.category}
            />
          ))}
        </div>

        <button className="carousel-control next" onClick={nextSlide}>
          &#10095;
        </button>
      </div>
    </div>
  );
};


export default ProductCarousel;
