import React, { useState, useEffect } from 'react';
import ProductCard from './productCard'; // Asegúrate de que el componente ProductCard esté importado

const ProductCarousel = ({ products }) => {

  const [currentIndex, setCurrentIndex] = useState(0);
  const [loading, setLoading] = useState(true); // Estado para manejar la carga
  console.log(products)
  useEffect(() => {
    // Solo cambia el estado de loading cuando los productos están listos
    if (products && products.length > 0) {
      setLoading(false); // Cuando los productos se reciban, cambia el estado de loading
    }
  }, [products]); // Reacciona al cambio de productos

  useEffect(() => {
    // Solo si los productos han sido cargados, empezamos el slider
    if (!loading && products.length > 0) {
      const interval = setInterval(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 3) % products.length); // Cambia de 3 en 3
      }, 3000);

      return () => clearInterval(interval); // Limpiar el intervalo cuando el componente se desmonte
    }
  }, [loading, products]);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 3) % products.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 3 + products.length) % products.length);
  };

  // Solo mostrar los productos si están disponibles
  const displayedProducts = products && products.length > 0 ? products.slice(currentIndex, currentIndex + 3) : [];

  // Mostrar mensaje mientras carga o si no hay productos
  if (loading) {
    return <p>Cargando productos...</p>; // Mostrar cuando aún no han llegado los productos
  }

  if (products.length === 0) {
    return <p>No hay productos disponibles.</p>; // Mostrar si no hay productos disponibles
  }

  return (
    <div className="product-carousel">
      <div className="carousel-container">
        <button className="carousel-control prev" onClick={prevSlide}>
          &#10094;
        </button>

        <div className="carousel-items">
          {displayedProducts.map((product) => (
            <ProductCard
              key={product._id}
              image={product.image}
              name={product.name}
              price={`$${product.price}`}
              category={product.category}
            />
          ))}
        </div>

        <button className="carousel-control next" onClick={nextSlide}>
          &#10095;
        </button>
      </div>
    </div>
  );
};

export default ProductCarousel;
