import React,{useState,useEffect} from "react";
import ProductCard from "../components/productCard"
import "../styles/styles.css";


<div class="carouselProducts">
  <div class="cartaProducto" id="card1"></div>
  <div class="cartaProducto" id="card2"></div>
  <div class="cartaProducto" id="card3"></div>
</div>

document.addEventListener("DOMContentLoaded",()=>{
  axios.get("http://localhost:5000/api/products")
    .then(response=>{
      const data=response.data;
      for(let i = 0; i< 3; i++){
        const card=document.getElementById(`card${i+1}`)
        card.innerHTML=`
          <img src="${data[i].imagenUrl}" alt="${data[i].nombre}">
          <h3>${data[i].nombre}</h3>
          <p>${data[i].descripcion}</p>
          <p>Precio: ${data[i].precio}</p>
        `;
      }
    }) 
    .catch(error => console.error('Error:', error)); 
})
