import React, { useState, useEffect } from 'react';
import ProductCard from './productCard';

const ProductCarousel = ({ products = [] }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [displayedProducts, setDisplayedProducts] = useState([]);

  // Duplicamos los productos para el efecto infinito
  useEffect(() => {
    if (products.length > 0) {
      setDisplayedProducts([...products, ...products, ...products]); // Duplicar productos
    }
  }, [products]);

  // Función para avanzar a la siguiente diapositiva
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % (products.length * 3));
  };

  // Función para retroceder a la diapositiva anterior
  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + products.length * 3) % (products.length * 3));
  };

  // Mostramos los productos visibles basados en el índice actual
  const visibleProducts = displayedProducts.slice(currentIndex, currentIndex + 3);

  return (
    <div className="product-carousel">
      <div className="carouselProducts-container">
        {/* Flecha para ir hacia la izquierda */}
        <button className="carouselProducts-control prev" onClick={prevSlide}>
          &#10094;
        </button>

        {/* Contenedor de productos */}
        <div className="carouselProducts-items">
          {visibleProducts.map((product, index) => (
            <div
              key={product._id}
              className={`carouselProduct-item ${index === 1 ? 'center' : ''}`}
            >
              <ProductCard
                key={product._id}
                image={product.image}
                name={product.name}
                price={`$${product.price}`}
                category={product.category}
              />
            </div>
          ))}
        </div>

        {/* Flecha para ir hacia la derecha */}
        <button className="carouselProducts-control next" onClick={nextSlide}>
          &#10095;
        </button>
      </div>
    </div>
  );
};

export default ProductCarousel;
